import { Component } from '@angular/core';
import { NgModel } from '@angular/forms';

@Component({
  selector: 'app-clogin',
  templateUrl: './clogin.component.html',
  styleUrls: ['./clogin.component.css']
})
export class CloginComponent {
  guardado = false;

  alumno = {
    nombre: '',
    clave: ''
  };

  guardar(){
    this.guardado = true;

  }

}


<div class = "card bg-primary p-2 mx-4 w-25" >
    <div class="card-body">
    <form>
        <div class="mb-2">
            <label class="mx-2 text-white">
                Usuario:
            </label>
            <input type="text" class="form-control"  name="formularioNombre" placeholder="Por favor Ingrese su nombre de usuario">
            <div class="aler alert-danger" [hidden]="">
                Llenar este campo
            </div>
        </div>
        <div class="mb-2">
            <label class="mx-2 text-white">
                Clave:
            </label>
            <input type="password" class="form-control" name="formularioClave" placeholder="Por favor Ingrese su clave">  
        </div>


    </form>

    <div>
        <button class="btn btn-primary mt-2 " (click)="guardar()" >Guardar</button></div>
        <div class="aler alert-danger mt-2" [hidden]="!guardado">
            Datos guardados con exito!
        </div>
    </div>


    </div>
